import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ViewComunicado = () => {
  const { id } = useParams();
  const [comunicado, setComunicado] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchComunicado = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/comunicado/${id}`);
        if (!response.ok) {
          throw new Error('Comunicado não encontrado.');
        }
        const data = await response.json();
        setComunicado(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchComunicado();
  }, [id]); // O useEffect será re-executado se o ID na URL mudar

  if (isLoading) {
    return <div className="text-center p-8 text-xl">Carregando comunicado...</div>;
  }

  if (error) {
    return <div className="text-center p-8 text-red-500 font-bold">{error}</div>;
  }

  return (
    <div className="container mx-auto p-4 max-w-3xl">
      <div className="bg-white p-8 rounded-lg shadow-lg">
        <h1 className="text-4xl font-extrabold text-gray-900 mb-2">{comunicado.titulo}</h1>
        <p className="text-lg text-indigo-600 mb-4 font-medium">{comunicado.beneficioPrincipal}</p>
        <p className="text-sm text-gray-500 mb-6">Entrega concluída em: {comunicado.data}</p>

        <p className="text-gray-700 leading-relaxed mb-6">{comunicado.descricao}</p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div className="p-4 bg-gray-50 rounded-md">
            <h2 className="font-semibold text-gray-800 mb-1">Antes</h2>
            <p className="text-gray-600 text-sm">{comunicado.impacto.antes}</p>
          </div>
          <div className="p-4 bg-gray-50 rounded-md">
            <h2 className="font-semibold text-gray-800 mb-1">Agora</h2>
            <p className="text-gray-600 text-sm">{comunicado.impacto.agora}</p>
          </div>
          <div className="p-4 bg-gray-50 rounded-md">
            <h2 className="font-semibold text-gray-800 mb-1">Resultado</h2>
            <p className="text-gray-600 text-sm">{comunicado.impacto.resultado}</p>
          </div>
        </div>

        <div className="border-t pt-6 mt-6">
          <p className="font-italic text-sm text-gray-500 leading-relaxed">{comunicado.inovacaoEmMovimento}</p>
        </div>

        <div className="text-center mt-8">
          <a
            href={`/feedback/${comunicado.tokenFeedback}`}
            className="inline-block px-8 py-3 border border-transparent text-base font-medium rounded-md text-white bg-green-600 hover:bg-green-700"
          >
            Avalie esta entrega
          </a>
        </div>
      </div>
    </div>
  );
};

export default ViewComunicado;
