import React from 'react';
import { Routes, Route, Link, useLocation } from 'react-router-dom';

// Importe as páginas que você criou
import CreateComunicado from './pages/Create';
import FeedbackPage from './pages/Feedback';
import AnalyticsPage from './pages/Analytics';
import ViewComunicado from './pages/View';

function App() {
  const location = useLocation();

  // Define as páginas que são de uso público
  const isPublicPage = location.pathname.startsWith('/comunicado/') || location.pathname.startsWith('/feedback/');

  return (
    <div className="min-h-screen bg-gray-100">
      
      {!isPublicPage && (
        <header className="bg-white shadow p-4 mb-8">
          <nav className="container mx-auto flex justify-between items-center">
            <Link to="/create" className="text-xl font-bold text-gray-800">
              Sistema de Comunicados
            </Link>
            <div className="space-x-4">
              <Link to="/create" className="text-blue-500 hover:text-blue-700 font-semibold">
                Criar Comunicado
              </Link>
              <Link to="/analytics" className="text-blue-500 hover:text-blue-700 font-semibold">
                Análise
              </Link>
            </div>
          </nav>
        </header>
      )}

      <main className="container mx-auto p-4">
        <Routes>
          <Route path="/create" element={<CreateComunicado />} />
          <Route path="/comunicado/:id" element={<ViewComunicado />} />
          <Route path="/feedback/:tokenFeedback" element={<FeedbackPage />} />
          <Route path="/analytics" element={<AnalyticsPage />} />
          <Route path="/" element={<CreateComunicado />} />
        </Routes>
      </main>
    </div>
  );
}

export default App;
