import React, { useState } from 'react';
import { useParams } from 'react-router-dom';

const FeedbackPage = () => {
  const { tokenFeedback } = useParams();
  const [nota, setNota] = useState(0);
  const [comentario, setComentario] = useState('');
  const [message, setMessage] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleRating = (value) => {
    setNota(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('Enviando...');
    
    try {
      const response = await fetch('http://localhost:3000/api/feedback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tokenFeedback, nota, comentario }),
      });

      if (response.ok) {
        setMessage('Obrigado! Seu feedback foi salvo.');
        setIsSubmitted(true);
      } else {
        setMessage('Ocorreu um erro ao enviar seu feedback. Tente novamente.');
      }
    } catch (error) {
      setMessage('Erro de conexão com o servidor.');
    }
  };

  return (
    <div className="container mx-auto p-4 max-w-xl text-center">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h1 className="text-2xl font-bold mb-4 text-gray-800">Avalie esta Entrega</h1>
        
        {!isSubmitted ? (
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="flex justify-center space-x-2 text-4xl">
              {[1, 2, 3, 4, 5].map((value) => (
                <span
                  key={value}
                  onClick={() => handleRating(value)}
                  className={`cursor-pointer transition-transform transform hover:scale-110 ${nota >= value ? 'text-yellow-400' : 'text-gray-300'}`}
                >
                  ⭐
                </span>
              ))}
            </div>

            <div>
              <label htmlFor="comentario" className="block text-sm font-medium text-gray-700">
                O que você achou?
              </label>
              <textarea
                id="comentario"
                value={comentario}
                onChange={(e) => setComentario(e.target.value)}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500"
                placeholder="Deixe um comentário (opcional)"
                rows="3"
              />
            </div>

            <button
              type="submit"
              disabled={!nota || isSubmitted}
              className="w-full px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              Enviar Feedback
            </button>
          </form>
        ) : (
          <div className="text-center">
            <p className="mt-4 text-green-600 font-medium text-lg">{message}</p>
            <p className="text-gray-500 mt-2">Seu feedback é muito importante para nós!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default FeedbackPage;
